  #{authorName,jdbcType=VARCHAR}, #{typeId,jdbcType=BIGINT}, #{publishStatus,jdbcType=BIT}, #{publishTm,jdbcType=TIMESTAMP},
  #{top,jdbcType=BIT}, #{speed,jdbcType=BIT}, #{createBy,jdbcType=BIGINT}, #{createTm,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=BIGINT},
