mapper.select:
select
, content
from t_article


    AND id = #{id, jdbcType=BIGINT}


    AND title = #{title,jdbcType=VARCHAR}


    AND author_id = #{authorId,jdbcType=BIGINT}


    AND author_name = #{authorName,jdbcType=VARCHAR}


    AND type_id = #{typeId,jdbcType=BIGINT}


    AND publish_status = #{publishStatus,jdbcType=BIT}



    AND publish_tm = #{publishTm,jdbcType=TIMESTAMP}


    AND top = #{top,jdbcType=BIT}


    AND speed = #{speed,jdbcType=BIT}



    AND create_by = #{createBy,jdbcType=BIGINT}



    AND create_tm = #{createTm,jdbcType=TIMESTAMP}


    AND update_by = #{updateBy,jdbcType=BIGINT}


    AND update_tm = #{updateTm,jdbcType=TIMESTAMP}


    AND version = #{version,jdbcType=INTEGER}


    AND content = #{content,jdbcType=LONGVARCHAR}



    AND  title like CONCAT(#{title,jdbcType=VARCHAR}, '%')



ORDER BY top, update_tm DESC

