mapper.insert:
insert into t_org_level (id, level_name, org_type_id,
  parent_level_id, create_by, create_tm, 
  update_by, update_tm, version
  )
values (#{id}, #{levelName,jdbcType=VARCHAR}, #{orgTypeId,jdbcType=BIGINT},
  #{parentLevelId,jdbcType=BIGINT}, #{createBy,jdbcType=BIGINT}, #{createTm,jdbcType=TIMESTAMP}, 
  #{updateBy,jdbcType=BIGINT}, #{updateTm,jdbcType=TIMESTAMP}, #{version,jdbcType=INTEGER}
  )

mapper.select:
SELECT  
FROM t_org_level


    AND level_name = #{levelName,jdbcType=VARCHAR}


    AND org_type_id = #{orgTypeId,jdbcType=BIGINT}


    AND parent_level_id = #{parentLevelId,jdbcType=BIGINT}


    AND create_by = #{createBy,jdbcType=BIGINT}


    AND create_tm = #{createTm,jdbcType=TIMESTAMP}


    AND update_by = #{updateBy,jdbcType=BIGINT}


    AND update_tm = #{updateTm,jdbcType=TIMESTAMP}


    AND version = #{version,jdbcType=INTEGER}


ORDER BY update_tm DESC
