mapper.select:
SELECT  
FROM t_org


    AND id = #{id,jdbcType=BIGINT}


    AND org_type_id = #{orgTypeId,jdbcType=VARCHAR}


    AND org_name = #{orgName,jdbcType=VARCHAR}


    AND parent_org_id = #{parentOrgId,jdbcType=VARCHAR}


    AND top_org_id = #{topOrgId,jdbcType=VARCHAR}


    AND sequence = #{sequence,jdbcType=INTEGER}


    AND level_id = #{levelId,jdbcType=BIGINT}


    AND level_name = #{levelName,jdbcType=VARCHAR}


    AND valid = #{valid,jdbcType=INTEGER}



    AND org_path = #{orgPath,jdbcType=VARCHAR}



    AND create_by = #{createBy,jdbcType=BIGINT}


    AND create_tm = #{createTm,jdbcType=TIMESTAMP}


    AND update_by = #{updateBy,jdbcType=BIGINT}


    AND update_tm = #{updateTm,jdbcType=TIMESTAMP}


    AND version = #{version,jdbcType=INTEGER}


    AND id in

      #{item}




    AND org_type_id in

      #{item}




ORDER BY update_tm DESC
