mapper.select:
SELECT

FROM t_org_type


        and org_type_name = #{orgTypeName,jdbcType=VARCHAR}


        and valid = #{valid,jdbcType=INTEGER}


        and  material_mark = #{materialMark,jdbcType=VARCHAR}


        and  bi_rel_type = #{biRelType,jdbcType=INTEGER}


        and create_by = #{createBy,jdbcType=BIGINT}


        and create_tm = #{createTm,jdbcType=TIMESTAMP}


        and update_by = #{updateBy,jdbcType=BIGINT}


        and update_tm = #{updateTm,jdbcType=TIMESTAMP}


        and version = #{version,jdbcType=INTEGER}



        and id in

            #{item}




        and org_type_name like concat(#{fuzzyOrgTypeName,jdbcType=VARCHAR}, '%')



ORDER BY create_tm ASC
